// src/components/Forum.jsx
import React, { useState, useEffect } from "react";

const Forum = () => {
  const [posts, setPosts] = useState(() => {
    const saved = localStorage.getItem("forumPosts");
    return saved ? JSON.parse(saved) : [];
  });

  const [postText, setPostText] = useState("");

  useEffect(() => {
    localStorage.setItem("forumPosts", JSON.stringify(posts));
  }, [posts]);

  const addPost = () => {
    if (postText) {
      const newPost = { id: Date.now(), text: postText, comments: [] };
      setPosts([newPost, ...posts]);
      setPostText("");
    }
  };

  const addComment = (postId, comment) => {
    if (!comment) return;
    setPosts(
      posts.map((p) =>
        p.id === postId
          ? { ...p, comments: [...p.comments, { id: Date.now(), text: comment }] }
          : p
      )
    );
  };

  return (
    <div className="content">
      <h2>ðŸ’¬ Student Forum</h2>
      <div className="event-form">
        <textarea
          placeholder="Share your thoughts..."
          value={postText}
          onChange={(e) => setPostText(e.target.value)}
        />
        <button onClick={addPost}>Post</button>
      </div>

      <div className="forum-section">
        {posts.map((post) => (
          <div key={post.id} className="forum-post">
            <p><strong>Post:</strong> {post.text}</p>
            <div className="comment-section">
              <h4>Comments:</h4>
              {post.comments.map((c) => (
                <p key={c.id}>â€¢ {c.text}</p>
              ))}
              <input
                type="text"
                placeholder="Add a comment..."
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    addComment(post.id, e.target.value);
                    e.target.value = "";
                  }
                }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Forum;
