// src/components/Placements.jsx
import React, { useState, useEffect } from "react";

const Placements = () => {
  const [placements, setPlacements] = useState(() => {
    const saved = localStorage.getItem("placements");
    return saved ? JSON.parse(saved) : [];
  });

  const [company, setCompany] = useState("");
  const [role, setRole] = useState("");
  const [salary, setSalary] = useState("");

  useEffect(() => {
    localStorage.setItem("placements", JSON.stringify(placements));
  }, [placements]);

  const addPlacement = () => {
    if (company && role && salary) {
      setPlacements([
        ...placements,
        { id: Date.now(), company, role, salary },
      ]);
      setCompany("");
      setRole("");
      setSalary("");
    }
  };

  const deletePlacement = (id) => {
    setPlacements(placements.filter((p) => p.id !== id));
  };

  return (
    <div className="content">
      <h2>ðŸ’¼ Placement Opportunities</h2>
      <div className="event-form">
        <input
          type="text"
          placeholder="Company Name"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
        />
        <input
          type="text"
          placeholder="Role"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        />
        <input
          type="text"
          placeholder="Package (LPA)"
          value={salary}
          onChange={(e) => setSalary(e.target.value)}
        />
        <button onClick={addPlacement}>Add</button>
      </div>

      <div className="event-list">
        {placements.map((p) => (
          <div key={p.id} className="event-card">
            <h3>{p.company}</h3>
            <p>Role: {p.role}</p>
            <p>Package: {p.salary} LPA</p>
            <button onClick={() => deletePlacement(p.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Placements;
